package PractisePrograms;

public class ReverseNumbers2 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
     int i;
     int j;
     int k;
     for(i=1; i<5 ; i++)
     {
    	 for(j=4; j>i; j--)
    	 {
    		 
    		 System.out.print(" ");
         }
    	 
    	 for(k=1; k<=i; k++)
    	 {
    		System.out.print(k); 
    	 }
    	 System.out.println(" ");
    }     
}

}
