package PractisePrograms;

public class Test2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i;
int j;
int k;

for(i=1;i<6;i++)
{
	for(j=5;j>i;j--)
	{
		System.out.print(" ");
		
	}
	for(k=1;k<=i;k++)
	{
		System.out.print("*");
	}
	
	System.out.println(" ");
}
	}

}
